import React, { useState } from 'react';
// import axios from "axios";
// import { iziToast } from 'izitoast';
// import 'react-toastify/dist/components/Toast'
import AppLogin from '../Api/ApiLogin';

const handleSubmit = (e) => {
    e.preventDefault();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
  
    let errorMessage = "";

    if (!username || !password) {
        errorMessage = "Username is required";
    } else if (!password) {
        errorMessage = "Password is required";
    }

    if (errorMessage) {
        setState({ errorMessage });
        return;
    }

    AppLogin.login(username,password)
    .then(result => {
        if(result){
            iziToast.success('You are now logged in', {
                position: 'topRight',
                timeout: 3000,
                onClosing: () => console.log('Closing')
            });
        }else{
            setState({ errorMessage: 'Invalid Credentials' });
        }
    })
    .catch(error => {
        iziToast.error(error.message, {
            position: 'topRight',
            timeout: 3000,
            onClosing: () => console.log('Closing')
        });
    })
  };



function MyForm() {
    
    return (

        <main>
        {/* <div className='container'>    */}
        <section class="section register min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
        <div class="container">
          <div class="row justify-content-center">
            <div class="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">

        <div class="card mb-3">
                <div class="card-body">

                  <div class="pt-4 pb-2">
                    <h5 class="card-title text-center pb-0 fs-4">Login to Your Account</h5>
                    <p class="text-center small">Enter your username & password to login</p>
                  </div>
                  <form className='row g-3 needs-validation' onSubmit={handleSubmit}>
                <div className='col-12'>
                    <div className='input-group has-validation'>
                    <input type='text' name='username' className='form-control' id='yourUsername' placeholder='masukan username' required pattern='^[a-zA-Z0-9]+$' onChange={handleChange}/>
                        <div class="invalid-feedback">Please enter your username.</div>
                    </div>
                </div>
                <div className='col-12'>
                    <div className='input-group has-validation'>
                        <input type='password' name='passwords' className='form-control'id='yourPassword'  placeholder='masukan password' />
                        <div class="invalid-feedback">Please enter your password.</div>
                    </div>
                </div>
                <div class="col-12">
                      <button class="btn btn-primary w-100" type="submit" >Login</button>
               </div>
            </form>
            </div>
            </div>
            </div>
            </div>
            </div>
            </section>
        {/* </div>     */}
    </main>
      
    )
  }
