            <input type="text" name="name1" onChange={(inp) => setInput1(inp.target.value)} />
            <input type="text" name="name2" onChange={(inp) => setInput2(inp.target.value)} />
            <button type='button' onClick={handleAddition}>=</button>
            <h3>Hasil:{App={}}</h3>
        </div>
    )
}
