import ActionType from "../Contants/ActionType";
import ApiUser from "../Service/ApiUser";

export const create = (user) => async (dispacth) => {
    try {
        const res = await ApiUser.create({
            user
        })
        dispacth({
            type: ActionType.add_user,
            payload: res.data
        })
        return Promise.resolve(res.data)
    } catch (error) {
        return Promise.reject(error)
    }
}

export const GetUser=() => async (dispacth) => {
    try {
        const res = await ApiUser.getAll()
        dispacth({
            type: ActionType.get_user,
            payload: res.data
        })
    } catch (error) {
        console.log({
            message: 'MasyaAlah Ada yang salah nih',
            error: error
        })
    }
}


export const UpdateUser = (id)=> async (dispacth) =>{
    try {
        const res = await ApiUser.update(id,data)
        dispacth({
            type:ActionType.update_user,
            payload:data
        })
        return Promise.resolve(res.data)
    } catch (error) {
        return Promise.reject(error)
        
    }
}


export const DeeteUser = (id) => async (dispacth) =>{
    try {
        const res = await ApiUser.delete(id)
        dispacth({
            type:ActionType.delete_user,
            payload:{id}
        })
        return Promise.resolve(res.data)
    } catch (error) {
        return promise.reject(error)
        
    }
}
