import config from './config'
import axios from 'axios'
const AppLogin={
    Login:async(username,passwords) =>{

        let option = {
            url : `http://localhost:3001/login`,
            method : 'POST',
            headers:{
                'context-Type' : 'application/json'
            },
            data:{
                username:username,
                password:passwords
            }
        }
        try{
            let result = await axios(option)
            return result
        }catch(error){
            return error
        }
    }
}
export default AppLogin
