import React, { useState } from 'react';

function Calculator(props) {
  const [total, setTotal] = useState(0);
  const [price, setPrice] = useState(10);

  const handleCheckboxChange = (isChecked, setChecked) => {
    setChecked(isChecked);
    calculate();
  };

  const handlePriceChange = event => {
    setPrice(event.target.value);
    calculate();
  };

  const calculate = () => {
    let newTotal = total;
    if (props.multiplyChecked) {
      newTotal *= price;
    }
    if (props.addChecked) {
      newTotal += price;
    }
    if (props.divideChecked) {
      newTotal /= price;
    }
    if (props.subtractChecked) {
      newTotal -= price;
    }
    setTotal(newTotal);
  }

  return (
    <div>
      <label>
        Perkalian:
        <input
          type="checkbox"
          checked={props.multiplyChecked}
          onChange={event => handleCheckboxChange(e.target.checked)}
        />
      </label>
      <label>
        Penambahan:
        <input
          type="checkbox"
          checked={props.addChecked}
          onChange={e => handleCheckboxChange(e.target.checked)}
        />
      </label>
      <label>
        Pembagian:
        <input
          type="checkbox"
          checked={props.divideChecked}
          onChange={e => handleCheckboxChange(e.target.checked)}
        />
      </label>
      <label>
        Pengurangan:
        <input
          type="checkbox"
          checked={props.subtractChecked}
          onChange={e => handleCheckboxChange(e.target.checked)}
        />
      </label>
      <label>
        Harga:
        <input type="number" value={price} onChange={handlePriceChange} />
      </label>
      <p>Total: {total}</p>
    </div>
  );
}

export default Calculator;
