import React from 'react'
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/components/Toast'
import AppLogin from '../Api/ApiLogin';
const handleSubmit = (e) => {
    e.preventDefault();
    const username = e.target.username.value
    const password = e.target.passwords.value

    AppLogin.login(username,password)
    .then(result => {
        if(result){
            ToastContainer.show({
                title: 'Success',
                message: 'You are now logged in',
                position: 'topRight',
                color: 'green',
                timeout: 3000,
                onClosing: () => console.log('Closing')
            });
        }else{
            ToastContainer.show({
                title: 'Error',
                message: 'Invalid credentials',
                position: 'topRight',
                color: 'red',
                timeout: 3000,
                onClosing: () => console.log('Closing')
            });
        }
    })
    .catch(error => {
        iziToast.show({
            title: 'Error',
            message: error.message,
            position: 'topRight',
            color: 'red',
            timeout: 3000,
            onClosing: () => console.log('Closing')
        });
    })
  };

function MyForm() {
    
    return (

      <ToastContainer />
      
    )
  }

export default MyForm
