import React from 'react'
import iziToast from 'izitoast';
const handleSubmit = (e) => {
    e.preventDefault();
    // Perform form submission logic here
    const username = e.target.username.value
    const password = e.target.passwords.value
    if(username === 'admin' && password === 'admin'){
      iziToast.show({
        title: 'Success',
        message: 'You are now logged in',
        position: 'topRight',
        color: 'green',
        timeout: 3000,
        onClosing: () => console.log('Closing')
      });
    }else{
      iziToast.show({
        title: 'Error',
        message: 'Invalid credentials',
        position: 'topRight',
        color: 'red',
        timeout: 3000,
        onClosing: () => console.log('Closing')
      });
    }
  };

function MyForm() {
    
    return (
        <main>
        {/* <div className='container'>    */}
        <section class="section register min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
        <div class="container">
          <div class="row justify-content-center">
            <div class="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">

        <div class="card mb-3">
                <div class="card-body">

                  <div class="pt-4 pb-2">
                    <h5 class="card-title text-center pb-0 fs-4">Login to Your Account</h5>
                    <p class="text-center small">Enter your username & password to login</p>
                  </div>
             <form className='row g-3 needs-validation' onSubmit={handlesubmit}>
                <div className='col-12'>
                    <div className='input-group has-validation'>
                        <input type='text' name='username' className='form-control' id='yourUsername' placeholder='masukan username' />
                        <div class="invalid-feedback">Please enter your username.</div>
                    </div>
                </div>
                <div className='col-12'>
                    <div className='input-group has-validation'>
                        <input type='password' name='passwords' className='form-control'id='yourPassword'  placeholder='masukan password' />
                        <div class="invalid-feedback">Please enter your password.</div>
                    </div>
                </div>
                <div class="col-12">
                      <button class="btn btn-primary w-100" type="submit">Login</button>
               </div>
            </form>
            </div>
            </div>
            </div>
            </div>
            </div>
            </section>
        {/* </div>     */}
    </main>
      
    )
  }

export default MyForm
