    const [price, setPrice] = useState(10);
  
    const handleCheckboxChange = isChecked => {
      if (isChecked) {
        setTotal(total + price);
      } else {
        setTotal(total - price);
      }
    };
  
    return (
      <div>
        <bu onCheckboxChange={handleCheckboxChange} />
        <p>Total: {total}</p>
      </div>
    );
  }

export default ParentComponent
