import config from "./config"

const AppLogin={
    login:async(username,password) =>{

        let option = {
            url : config .AppLogin,
            method : 'POST',
            headers:{
                'context-Type' : 'application/json'
            },
            data:{
                username:username,
                password:password
            }
        }
        try{
            let result = await axios(option)
            return result.data
        }catch(error){
            return error
        }
    }
}
export default AppLogin
