import React, { useState } from 'react'


export default function HelloWord() {
    const [count,set]=useState(0)
    let increment =()=>{
        set(count+1)
    }
    let dicrement =()=>{
        set(count-1)
    }
  return (
    <div>
    <h2>Penjumlahan</h2>
    <h3>Counter:{count}</h3>
      <button type='button' onClick={increment}>+
     </button>     
      <button type='button' onClick={dicrement}>-
     </button>     
    </div>
  )
}
