import React, { useState } from 'react';

function Calculator(props) {
  const [total, setTotal] = useState(0);
  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);

  const handleCheckboxChange = (isChecked, setChecked) => {
    setChecked(isChecked);
    calculate();
  };

  const handleNum1Change = event => {
    setNum1(event.target.value);
    calculate();
  };
  const handleNum2Change = event => {
    setNum2(event.target.value);
    calculate();
  };

  const calculate = () => {
    let newTotal = total;
    if (props.multiplyChecked) {
      newTotal = num1 * num2;
    }
    if (props.addChecked) {
      newTotal = num1 + num2;
    }
    if (props.divideChecked) {
      newTotal = num1 / num2;
    }
    if (props.subtractChecked) {
      newTotal = num1 - num2;
    }
    setTotal(newTotal);
  }

  return (
    <div>
      <label>
        Perkalian:
        <input
          type="checkbox"
          checked={props.multiplyChecked}
          onChange={e => handleCheckboxChange(e.target.checked, setMultiplyChecked)}
        />
      </label>
      <label>
        Penambahan:
        <input
          type="checkbox"
          checked={props.addChecked}
          onChange={e => handleCheckboxChange(e.target.checked, setAddChecked)}
        />
      </label>
      <label>
        Pembagian:
        <input
          type="checkbox"
          checked={props.divideChecked}
          onChange={e => handleCheckboxChange(e.target.checked, setDivideChecked)}
        />
      </label>
      <label>
        Pengurangan:
        <input
          type="checkbox"
          checked={props.subtractChecked}
          onChange={e => handleCheckboxChange(e.target.checked, setSubtractChecked)}
        />
      </label>
      <label>
        Angka 1:
        <input type="number" value={num1} onChange={handleNum1Change} />
      </label>
      <label>
        Angka 2:
        <input type="number" value={num2} onChange={handleNum2Change} />
      </label>
      <p>Total: {total}</p>
    </div>
  );
  }
export default Calculator
