import React, { useState } from 'react'
import AppCss from '../App.css';
import App from '../App';

export default function BelajarBerhitung() {
    // const [count,set]=useState(0)
    // let increment =()=>{
    //     set(count+1)
    // }
    // let dicrement =()=>{
    //     set(count-1)
    // }
        const [input1, setInput1] = useState(0);
        const [input2, setInput2] = useState(0);
        const [result, setResult] = useState(0);
    function Perjumlahan() {
        const result = input1 + input2;
        setResult(result);
    }
    const[styles,setStyle]= AppCss()
    function Style(){
        const margin = styles.AppCss('margin:12px')
        setStyle=margin
    }
    return (
        <div>
            <h2>Penjumlahan</h2>
            < input type="text" name="name1" onChange={(e) => setInput1(e.target.value)} placeholder='Masukan Angka' />
            <br></br>
            <input type="text" name="name2" onChange={(e) => setInput2(e.target.value)} placeholder='Masukan Angka'/>
            <br></br>
            <button type='button' onClick={Perjumlahan}>Jumlahkan</button>
            <h3>Hasil:{result}</h3>
        </div>
    )
}
