function MyForm() {
  const [username, setUsername] = useState('');
  const [passwords, setPasswords] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // validasi form
    if (!username || !passwords) {
      setError('Please enter a username and password');
      return;
    }
    // cek login
    if (username === 'admin' && passwords === 'admin') {
      setError('benar');
      // redirect ke halaman berikutnya atau lakukan aksi lain
    } else {
      setError('Invalid username or password');
    }
  };
  
    return (
        <main>
     <section class="section register min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
     <div class="container">
       <div class="row justify-content-center">
         <div class="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">

      <div class="card mb-3">
             <div class="card-body">

               <div class="pt-4 pb-2">
                 <h5 class="card-title text-center pb-0 fs-4">Login to Your Account</h5>
                 <p class="text-center small">Enter your username & password to login</p>
               </div>
      <form onSubmit={handleSubmit}>
                {error && <p style={{ color: 'red' }}>{error}</p>}
        <label>
          Username:
          <input type='text' name='username' className='form-control' id='yourUsername' placeholder='masukan username' required 
                      placeholder='masukan username' value={username} onChange={e => setUsername(e.target.value)} />
        </label>
        <br />
        <label>
          Password:
          <input type='Password' name='paswords' className='form-control'id='yourPassword'
                      placeholder='masukan Password' value={passwords} onChange={e => setPasswords(e.target.value)} />
        </label>
        <br />

        <div class="col-12">
     <button class="btn btn-primary w-100" type="submit" >Login</button>
</div>
      </form>
    </div>
    </div>
    </div>
    </div>
    </div>
    </section>
    </main>
    );
  }
  

export default MyForm



