import React, { useState } from 'react'

export default function Pengurangan() {
    const [input1, setInput1] = useState(0);
    const [input2, setInput2] = useState(0);
    const [result, setResult] = useState(0);
    const [operatorChecked, setOperatorChecked] = useState(false);
    function Pertambahan() {
        const result = parseInt(input1 + input2);
        setResult(result);
    }
    function Pengurangan() {
        const result = parseInt(input1 + input2);
        setResult(result);
    }
    function Perkalian() {
        const result = parseInt(input1 * input2);
        setResult(result);
    }
    function Pembagian() {
        const result = parseInt(input1 / input2);
        setResult(result);
    }
    const perhitungan =()=>{
        if (Pertambahan() ) {
            
        }
        Pertambahan()
        Pengurangan()
        Perkalian()
        Pembagian()
    }
    const checkOperator =()=>{
        setOperatorChecked(true);
    }
    return (
        <div className='style'>
            <h2>Pengurangan</h2>
            <div className=''>
            <input type="number"  name="name1"  id='input1' onChange={(e) => setInput1(parseInt( e.target.value))} placeholder='Masukan Angka' />
            &nbsp;  &nbsp; 
            <input type="number" name="name2"  id='input2' onChange={(e) => setInput2(parseInt(e.target.value))} placeholder='Masukan Angka' onBlur={checkOperator}/>
            </div>
            <div className='mt-3'>
            {operatorChecked ? 
                <button type='button' onClick={perhitungan} className='btn btn-outline-primary btn-lg'>Jumlahkan</button>
                : 
                <p>Silahkan isi kedua input field</p>
            }
            </div>
            <br></br>
            <div className=''>
            <input type="number" name="name3"  id='input2'value={result} placeholder='Masukan Angka' readonly/>
            </div>
        </div>
    )
}
