function MyForm() {
    const [username, setUsername] = useState('');
    const [passwords, setPasswords] = useState('');
    const [error, setError] = useState('');
    
    const handleSubmit = async (e) => {
    e.preventDefault();
    try {
    const result = await AppLogin.login(username, passwords);
    if (result) {
    console.log('You are now logged in');
    } else {
    setError('Invalid credentials');
    }
    } catch (err) {
    setError(err.message);
    }
    };
  
    return (
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input  type='text' name='username' className='form-control' id='yourUsername'
                      placeholder='masukan username' value={username} onChange={e => setUsername(e.target.value)} />
        </label>
        <br />
        <label>
          Password:
          <input type='Password' name='paswords' className='form-control' id='yourUsername'
                      placeholder='masukan Password' value={passwords} onChange={e => setPasswords(e.target.value)} />
        </label>
        <br />
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Login</button>
      </form>
    );
  }
  

export default MyForm
