import config from '../Config/config'
import axios from 'axios'

const AppLogin = {
    Login: async (username, password) => {
        let option = {
            url: config.base_url + '/login/save',
            method: 'POST',
            headers: {
                'context-Type': 'Application/json'
            },
            data: {
                username: username,
                password: password
            }
        }
        
        try {
            let result = await axios(option)

            return result.data
        } catch (error) {
            return error
        }
    }
}

export default AppLogin
