import config from '../Config/config'
import axios from 'axios'
const AppLogin={
    Login: async(username,passwords) =>{

        let option = {
            url : config.baseUrl + 'login',
            method : 'POST',
            headers:{
                'context-Type' : 'Application/json'
            },
            data:{
                username:username,
                passwords:passwords
            }
        }
        try{
            let result = await axios(option)
            return result.data
        }catch(error){
            return error
        }
    }
}
export default AppLogin
